// GarticPhone Mod - Main UI Script
(function() {
    'use strict';
    
    console.log('GarticPhone Mod UI loaded');
    
    const MOD = {
        version: '1.0.0',
        
        showNotification: function(message, type = 'info', duration = 3000) {
            const notification = document.createElement('div');
            notification.className = 'gp-mod-notification';
            notification.textContent = message;
            
            if (type === 'success') {
                notification.style.background = 'linear-gradient(135deg, #4ade80, #22c55e)';
            } else if (type === 'error') {
                notification.style.background = 'linear-gradient(135deg, #f87171, #ef4444)';
            } else if (type === 'warning') {
                notification.style.background = 'linear-gradient(135deg, #fbbf24, #f59e0b)';
            }
            
            document.body.appendChild(notification);
            
            setTimeout(() => {
                notification.style.animation = 'gp-mod-slide-in 0.3s ease reverse';
                setTimeout(() => notification.remove(), 300);
            }, duration);
        },
        
        createButton: function(text, onClick, options = {}) {
            const button = document.createElement('button');
            button.className = 'gp-mod-button';
            button.textContent = text;
            button.addEventListener('click', onClick);
            
            if (options.id) button.id = options.id;
            if (options.style) Object.assign(button.style, options.style);
            
            return button;
        },
        
        injectStyles: function(css) {
            const style = document.createElement('style');
            style.textContent = css;
            document.head.appendChild(style);
        },
        
        // Example feature: Add mod indicator
        addModIndicator: function() {
            const indicator = document.createElement('div');
            indicator.innerHTML = 'ðŸŽ® GP Mod v' + this.version;
            indicator.style.cssText = `
                position: fixed;
                bottom: 10px;
                right: 10px;
                background: rgba(0, 0, 0, 0.7);
                color: #4cc9f0;
                padding: 5px 10px;
                border-radius: 5px;
                font-size: 12px;
                z-index: 99998;
                font-family: monospace;
            `;
            document.body.appendChild(indicator);
        }
    };
    
    // Add to window object
    window.gpMod = MOD;
    
    // Initialize when page is ready
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', () => MOD.addModIndicator());
    } else {
        MOD.addModIndicator();
    }
    
})();
